import React, { useState, useEffect, Fragment, useContext } from "react";
import RowComponents from './RowComponent'
import './App.css';

function App() {
  const count = React.useRef([{ text: "Level 1", value: "Level 1", parent: 0}]);
  const [tempCount, setTempCount] = React.useState([
    { name: "option", value: "", check: false },
  ]);

  const removeRow = (index) => {
    const newArr = [...count.current];
    newArr.splice(index, 1);
    count.current = [...newArr];
    setTempCount([...newArr]);
  };

  const updateRow = (index, e) => {
    if (count.current.find((url) => url.value === e.target.value)) {
      const newArr = [...count.current]; // copying the old datas array
      newArr[index].value = "Duplicate"; // replace e.target.value with whatever you want to change it to
      count.current = newArr;
    } else {
      const newArr = [...count.current]; // copying the old datas array
      newArr[index].value = e.target.value; // replace e.target.value with whatever you want to change it to
      count.current = newArr;
    }
  };

  return (
    <div className="App">
      {count.current.map((item, index) => (
        <>
        <RowComponents  onChange={(e) => updateWebsiteUrl(index, e)}   key={index}/>

        </>
      ))}
    </div>
  );
}

export default App;
