import React from "react";

function RowComponents(props){

    return (
        <>
        <div className="row">
            {console.log('props.text',props.text)}
            <input value={props.text} onChange={(e) => { props.updateRow(props.index, e.target.value); console.log('hey',e.target.value)}}/>
            <button onClick={props.addRow}>Add</button>
            <button onClick={(e) => { props.updateRow(props.index, e.target.value)}}>Edit</button>
            <button onClick={() => {props.removeRow(props.index)}}>Remove</button>
        </div>
        </>
    )
} 

export default RowComponents;
