import React, { useState } from "react";

function RowComponents(props){
    const [name, setName] = useState(props.name);
    const [showInput, setShowInput] = useState(false);
    const [inputText, setInputText] = useState('');

    return (
        <>
            <div className="d-flex mt-10">
            <input value={name} onChange={(e) => { setName(e.target.value) }} className='mr-10'/>
            <button onClick={() => { props.addRow(props.id); setShowInput(true); }}>Add</button>
            {
                name && name == '' && <button onClick={() => { props.save(name, props.item?.parent_id) }}>save</button>  
            }
            <button onClick={() => { props.edit(name, props.item.id) }}>Edit</button>
            <button>Remove</button>
            </div>
            { (props.item.sub) ? props.item.sub.map((sub) => (
            <div className="p-5">
                <RowComponents name={sub.name} addRow={props.addRow} id={sub.id} parent_id={sub.parent_id} save={props.createNew} item={sub} edit={props.edit}/> 
            </div>
            )): '' }
            {
                showInput && <input value={inputText} onChange={(e) => {setInputText(e.target.value)}}/>
            }
           
        </>
    )
} 

export default RowComponents;
