import React, { useState, useEffect } from "react";
import RowComponents from './RowComponent';
import './App.css';
import axios from 'axios';

function App() {
  const [temp, setTemp] = useState(1);
  const [arr, setArr] = useState([
    { id: 0, name: "", parent: 0 },
  ]);
  const [data, setData] = useState();

  useEffect(() => {
    getAllData();
  },[]);

  const getAllData = async () => {
    // console.log('data', await fetch('http://localhost:8000/getAllCategory'));
    axios.get('http://localhost:8000/getAllCategory').then((res) => {
      console.log('res',res);
      if(res.data) {
        setData(res.data);
      } else {
        setData([ { id: 0, name: "", parent: 0 }]);
      }
    }).catch((err) => {
      console.log(err);
    })
  }

  const addRow = (id) => {
    let newArr = [...data];
    newArr.push({
      id: '', 
      name: "",
      parent: id 
    })
    setData(newArr);
  }
  console.log('arr',arr)
  const increseCount = (id) => {
    setTemp(temp+1);
    addRow(id);
  }
  return (
    <div className="App">
      { data && data.map((item) => (
        <>
          <RowComponents name={item.name} addRow={increseCount} id={item.id}/>
        </>
      )) }
    </div>
  );
}

export default App;
