import React, { useState, useEffect } from "react";
import RowComponents from './RowComponent';
import './App.css';
import axios from 'axios';

function App() {
  const [temp, setTemp] = useState(1);
  const [arr, setArr] = useState([
    { id: 0, name: "", parent: 0 },
  ]);
  const [data, setData] = useState();

  useEffect(() => {
    getAllData();
  },[]);

  const getAllData = async () => {
    // console.log('data', await fetch('http://localhost:8000/getAllCategory'));
    axios.get('http://localhost:8000/getAllCategory').then((res) => {
      if(res.data) {
        setData(res.data);
      } else {
        setData([ { id: 0, name: "", parent: 0 }]);
      }
    }).catch((err) => {
      console.log(err);
    })
  }

  const addRow = (id) => {
    let newArr = [...data];
    newArr.push({
      id: '', 
      name: "Category Name",
      parent: id 
    })
    setData(newArr);
  }
  console.log('arr',data)
  const increseCount = (id) => {
    setTemp(temp+1);
    addRow(id);
  }

  const createNew = (name, parent_id) => {
    console.log('name',name)
    let body = [];
    body['name'] = name;
    body['parent_id'] = parent_id;
    console.log('bosy', body);
    axios.post('http://localhost:8000/store', body).then((res) => {
      console.log('post',res);
      getAllData()
    })
  }
  return (
    <div className="App">
      { data && data.map((item) => (
        <>
        {/* {console.log('item',item)} */}
          <RowComponents name={item.name} addRow={increseCount} id={item.id} parent_id={item.parent_id} save={createNew} item={item}/>
          { (item.sub) ? item.sub.map((sub) => (
           <div className="p-5">
              <RowComponents /> 
           </div>
          )): '' }
        </>
      )) }
    </div>
  );
}

export default App;
