import React, { useState, useEffect } from "react";
import RowComponents from './RowComponent';
import './App.css';
import axios from 'axios';

function App() {
  const [temp, setTemp] = useState(1);
  const [arr, setArr] = useState([
    { id: 0, name: "", parent: 0 },
  ]);
  const [data, setData] = useState();

  useEffect(() => {
    getAllData();
  },[]);

  const getAllData = async () => {
    // console.log('data', await fetch('http://localhost:8000/getAllCategory'));
    axios.get('http://localhost:8000/getAllCategory').then((res) => {
      if(res.data) {
        console.log('formated Array',res.data)
        setData(res.data);
      } else {
        setData([ { id: 0, name: "", parent: 0 }]);
      }
    }).catch((err) => {
      console.log(err);
    })
  }

  console.log('arr',data)
  const increseCount = (id) => {
    setTemp(temp+1);
    addRow(id);
  }

  const createNew = (name, parent_id) => {
    console.log('name',name)
    let body = {};
    body['name'] = name;
    body['parent_id'] = parent_id;
    console.log('bosy', body);
    axios.post('http://localhost:8000/store', body).then((res) => {
      console.log('post',res);
      getAllData()
    })
  }

  const edit = (name, id) => {
    let body = {};
    body['name'] = name;
    console.log('body',body)
    axios.put(`http://localhost:8000/update/${id}`, body).then((res) => {
      console.log('post',res);
      getAllData()
    })
  }

  const deleteCategory = (id) => {
    axios.delete(`http://localhost:8000/delete/${id}`).then((res) => {
      getAllData()
    })
  }

  return (
    <div className="App">
      { data && data.map((item) => (
        <>
        {/* {console.log('item',item)} */}
          <RowComponents name={item.name} addRow={createNew} id={item.id} parent_id={item.parent_id} save={createNew} item={item} edit={edit} deleteCategory={deleteCategory}/>

        </>
      )) }
      {console.log('addRow',data)}
    </div>
  );
}

export default App;
