import React, { useState, useEffect, Fragment, useContext } from "react";

function RowComponents(props){
    console.log('props',props)
    return (
        <>
        <div className="row">
            <input value={props.text} onChange={(e) => {props.updateRow(props.index, e)}}/>
            <button onClick={props.addRow}>Add</button>
            <button onClick={(e) => { props.onChange(props.index, e) }}>Edit</button>
            <button >Remove</button>
        </div>
        </>
    )
} 

export default RowComponents;
