  const [data, setData] = useState();
  useEffect(() => {
    getAllData();
  },[]);

  const getAllData = async () => {
    console.log('data', await fetch('http://localhost:8000/getAllCategory'));
    // axios.get('http://localhost:8000/getAllCategory').then((res) => {
    //   console.log('res',res);
    // }).catch((err) => {
    //   console.log(err);
    // })
  }

  const [temp, setTemp] = useState(1);
  const [arr, setArr] = React.useState([
    { id: 0, name: "", parent: 0 },
  ]);

  const addRow = (id) => {
    let newArr = [...arr];
    newArr.push({
      id: temp, 
      name: "",
      parent: id 
    })
  }

  const increseCount = (id) => {
    setTemp(temp+1);
    addRow(id);
  }
  return (
    <div className="App">
      { arr.map((item) => (
        <>
          <RowComponents name={item.name} addRow={increseCount} id={item.id}/>
        </>
      )) }
    </div>
  );
}

export default App;
