    //   newArr[index].value = e.target.value; // replace e.target.value with whatever you want to change it to
    //   count.current = newArr;
    // }
    const newArr = [...tempCount]; 
    newArr[index].text = e.target.value;
    setTempCount(newArr);
  };
  console.log('tempCount',tempCount)
  const addRow = () => {
    setTempCount([
      ...tempCount,
      {
        text: 'write',
        value: "",
        parent: 0,
      },
    ]);
  } 

  {console.log('count',count)}

  return (
    <div className="App">
      {tempCount.map((item, index) => (
        <>
        <RowComponents  onChange={(e) => updateRow(index, e)} index={index} text={item.text} addRow={addRow}/>

        </>
      ))}
      {/* <RichObjectTreeView /> */}
    </div>
  );
}

export default App;
